mapper.select:
select
id, goods_id, store_id, quantity, sum_price
from goods_stores


        and id = #{id}


        and goods_id = #{goodsId}


        and store_id = #{storeId}


        and quantity = #{quantity}


        and sum_price = #{sumPrice}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from goods_stores


        and id = #{id}


        and goods_id = #{goodsId}


        and store_id = #{storeId}


        and quantity = #{quantity}


        and sum_price = #{sumPrice}

select count(1)
from goods_stores
where goods_id = #{goodsId} and store_id = #{storeId}

select count(1)
from goods_stores
where goods_id = #{goodsId} and inbound_date >= #{inboundDate}
